mapper.insert:
insert into transaction_local (hash, type, tx_index,
    block_height, create_time, fee,transferType,
    remark, txData, coinData, scriptSig, size,txStatus
)
values (#{hash,jdbcType=VARCHAR}, #{type,jdbcType=INTEGER}, #{txIndex,jdbcType=INTEGER},
    #{blockHeight,jdbcType=BIGINT}, #{createTime,jdbcType=BIGINT}, #{fee,jdbcType=BIGINT},
    #{transferType,jdbcType=INTEGER}, #{remark,jdbcType=VARCHAR}, #{txData,jdbcType=LONGVARBINARY},
    #{coinData,jdbcType=LONGVARBINARY}, #{scriptSig,jdbcType=VARBINARY}, #{size,jdbcType=INTEGER},
    #{txStatus,jdbcType=INTEGER}
)
